mapper.update:
UPDATE ord_base
SET review_yn = 'Y'
WHERE ORD_NO = #{ordNo}

mapper.insert:
INSERT INTO review
( prod_code
, u_id
, content
, star
, ord_no
, rgs_time
) VALUES
    ( #{prodCode}
    , #{uId}
    , #{content}
    , #{star}
    , #{ordNo}
    , NOW())

mapper.select:
SELECT PROD_CODE
FROM ord_base
WHERE ord_no = #{ordNo}
